package com.wipro.basics;

public class DataTypes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        //I want to save the details of my trainer --- > name , trainer qualification, trainer proficiency , trainer's contact details
		// where to store and how to store  (Variable)
		// which type of data you are storing (Data type)
		
		
	    // Non-primitive data types 
		String name = "Niti Dwivedi" ;  // holding the alphanumeric 
		
		System.out.println(name);	
		
		//primitive data types 
		char gender = 'f';
		
		int contact = 99345;
		
		byte a = 127; // convert this short to byte ,,  short has the higher capacity to store the values 
		
		short b = a;  // implicit casting  // lower to higher 
		
		short a1 = 456;
		
		byte b1 = (byte)a1; // explicit casting // higher to lower 
		
		float salary = 456.67f;
		
		double bonus = 345.78;
		
		float f = 123.34f;
		
		int x1 = (int)f;
		
		//Integer : Wrapper Class -  byte,short, int, long
		//Float
		//Double
		//Character
		
	}

}

