package com.wipro.basics;

public class IfExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
         int num = 25; // declaration & initialization
		
         if(num > 0)   //first condition 
			
		{
			if (num % 2 == 0 ) // modulo operator which will return remainder
			System.out.println(" The no. is +ve and even");
			
		}
		
		else
		{
			System.out.println("The no. is +ve and odd");
		}
		
	}
	

}
